// All shared data structures in AUX RAM need to be packed
#pragma pack(push, 2)


/// TaskHDC1080: Task output data structure
typedef struct {
    uint16_t devID; ///< 
    uint16_t hum;   ///< 
    uint16_t manID; ///< 
    int16_t  tmp;   ///< 
} SCIF_HDC1080_TASK_HDC1080_OUTPUT_T;


/// TaskHDC1080: Task state structure
typedef struct {
    uint16_t i2cStatus; ///< I2C master status
} SCIF_HDC1080_TASK_HDC1080_STATE_T;


/// Sensor Controller task data (configuration, input buffer(s), output buffer(s) and internal state)
typedef struct {
    struct {
        SCIF_HDC1080_TASK_HDC1080_OUTPUT_T output;
        SCIF_HDC1080_TASK_HDC1080_STATE_T state;
    } taskHdc1080;
} SCIF_HDC1080_TASK_DATA_T;

/// Sensor Controller task generic control (located in AUX RAM)
#define scifHdc1080TaskData    (*((volatile SCIF_HDC1080_TASK_DATA_T*) 0x400E00EA))


// Initialized internal driver data, to be used in the call to \ref scifInit()
extern const SCIF_DATA_T scifHdc1080DriverSetup;


// Restore previous struct packing setting
